import React, { useState, useEffect} from "react";
import Axios from "axios";
import { Link } from 'react-router-dom';
import Modal from "./Components/Modal";

const Lista = () =>{

    const [listaAlunos, setListaAlunos] = useState([]);

    useEffect(() => {
        Axios.get("http://localhost:3001/lista/api/get").then((response) => {
          setListaAlunos(response.data);
        })
        console.log(listaAlunos);
      }, []);

    //Função para deletar aluno
  const deletaAluno = (delAluno) => {
    Axios.delete(`http://localhost:3001/lista/api/delete/${delAluno}`);
  };

  return (
    <div>      
      <header>
        <div id="menu">
            <div><Link to="/" style={{color: "white"}}>Home</Link></div> 
            <div><Link to="/aluno" style={{color: "white"}}>Aluno</Link></div>
            <div><Link to="/disciplina" style={{color: "white"}}>Disciplina</Link></div>
            <div><Link to="/lista" style={{color: "white"}}>Lista</Link></div>
        </div>
    </header>

    <div className="row">
    <table>
        <tr>
            <th>Nome</th>
            <th>Email</th>
            <th>Naturalidade</th>
            <th>Disciplina</th>
            <th>Professor</th>
            <th></th>
        </tr>

    {listaAlunos.map((val) => {
        return (            
                <tr key={val.id}>
                    <td>{val.nome}</td>
                    <td>{val.email}</td>
                    <td>{val.naturalidade}</td>
                    <td>{val.disciplina}</td>
                    <td>{val.professor}</td>
                    <td><button>Atualizar</button>
                    <button onClick={() => {deletaAluno(val.id)}}>Deletar</button></td>
                </tr>


            /* <div key={val.id} className="row">
                <p hidden>{val.id}</p>
                <h1>{val.nome}</h1>
                <p>Email: {val.email}</p>
                <p>Naturalidade: {val.naturalidade}</p>
                <p>Disciplina: {val.disciplina}</p>
                <p>Professor: {val.professor}</p>

                <button>Editar</button>
                <button>Deletar</button>
            </div> */
        )
    })}
    
    </table>
    </div>
    
       
    </div>
  );
}

export default Lista;
