import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import Modal from 'react-modal';

function ModalJanela({closeModal, id}) {
  const [nome, setNome] = useState ('');
  const [email, setEmail] = useState ('');
  const [naturalidade, setNaturalidade] = useState ('');
  const [disciplina, setDisciplina] = useState ('');

  const [listaDisciplinas, setListaDisciplinas] = useState ([]);
  const [aluno, setAluno] = useState ({});

  useEffect (() => {
    Axios.get ('http://localhost:3001/Modal/api/get').then (response => {
      setListaDisciplinas (response.data);
      console.log (id);
    });
    Axios.get (`http://localhost:3001/Modal/api/get/${id}`).then (r => {
      setAluno (r.data[0]);
      console.log(r.data[0])      
    });
    setNome (aluno.nome)
    console.llunog(nome)
    setEmail (ao.email)
    setNaturalidade (aluno.naturalidade)
  }, []);

  //Função para cadastrar aluno
  const cadastraAluno = () => {
    Axios.post ('http://localhost:3001/Modal/api/insert', {
      nome: nome,
      email: email,
      naturalidade: naturalidade,
      disciplina: disciplina,
    });
  };
  return (
    <Modal isOpen={closeModal}>

      <div className="modalBackground">

        <button onClick={() => closeModal (false)}>X</button>

        <div className="form">

          <label>Nome</label>
          <input
            type="text"
            name="nome"
            value={nome}
            onChange={n => {
              setNome (n.target.value);
            }}
          />
          <label>Email</label>
          <input
            type="text"
            name="email"
            value={email}
            onChange={e => {
              setEmail (e.target.value);
            }}
          />
          <label>Naturalidade</label>
          <input
            type="text"
            name="Naturalidade"
            value={naturalidade}
            onChange={b => {
              setNaturalidade (b.target.value);
            }}
          />
          <label>Disciplina</label>
          <select
            type="text"
            name="selecao"
            onChange={d => {
              setDisciplina (d.target.value);
            }}
          >
            <option value="0">Selecione disciplina</option>
            {listaDisciplinas.map (val => (
              <option key={val.id} value={val.id}>{val.disciplina}</option>
            ))}
          </select>

          <button onClick={cadastraAluno}>Cadastrar</button>

        </div>

      </div>

    </Modal>
  );
}

export default ModalJanela;
