import React, { useState, useEffect} from "react";
import Routes from "./routes";
import Footer from "./Footer";							  
import Axios from "axios";
import './App.css';

function App() {

  const [disciplina, setDisciplina] = useState("");
  const [professor, setProfessor] = useState("");
  const [listaDisciplinas, setListaDisciplinas] = useState([]);

  const [newProfessor, setNewProfessor] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/disciplina/api/get").then((response) => {
      setListaDisciplinas(response.data);
    })
  }, [newProfessor]);

  //Função para cadastrar disciplina
  const cadastraDisciplina = () => {
    Axios.post("http://localhost:3001/disciplina/api/insert", {disciplina: disciplina, professor: professor,});

    setListaDisciplinas([...listaDisciplinas, {disciplina: disciplina, professor: professor},]);

  };

  //Função para deletar disciplina
  const deletaDisciplina = (delDisciplina) => {
    Axios.delete(`http://localhost:3001/disciplina/api/delete/${delDisciplina}`);
  };

  //Função para atualizar disciplina
  const atualizaProfessor = async(oldId) => {
    console.log(newProfessor);
    await Axios.put("http://localhost:3001/disciplina/api/update", {id: oldId, professor: newProfessor,});

    setNewProfessor("");

  };

  return (
    <div className="App">
      <Routes/>
      <Footer/>
    </div>
  );
}

export default App;
