import React, { useState, useEffect} from "react";
import Axios from "axios";
import { Link } from "react-router-dom";

const Aluno = () => {

  const [listaDisciplinas, setListaDisciplinas] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/aluno/api/get").then((response) => {
      setListaDisciplinas(response.data);
      console.log(listaDisciplinas);
    })
  }, []);

    return (
        <div>
        <header>
        <div id="menu">
            <div><Link to="/" style={{color: "white"}}>Home</Link></div> 
            <div><Link to="/aluno" style={{color: "white"}}>Aluno</Link></div>
            <div><Link to="/Disciplina" style={{color: "white"}}>Disciplina</Link></div>
        </div>
    </header>

<div className="form">    
      
      <label>Nome</label>
      <input className="input-text" placeholder="Nome" type="text" name="nome"/>
      <label>Email</label>
      <input className="input-text" placeholder="Email" type="text" name="email"/>
      <label>Naturalidade</label>
      <input className="input-text" placeholder="Naturalidade" type="text" name="Naturalidade"/>
      <label>Disciplina</label>
            <select className="input-text" name="selecao">
                <option value="0">Selecione disciplina</option>
                {listaDisciplinas.map((disciplina) => <option value={disciplina.id}>{disciplina.nome}</option>)}
            </select>
      <center>
      <button className="input-btn">Cadastrar</button>
      </center>
      

      </div>
      </div>
    );
}

export default Aluno;
